$(function(){
	var cityData;
	var cityRegNum;
	var cityLoanMoney;
	var cityRepayMoney;
	cvphp.get(
		apiUrl,
		{},
		function(data){
			if(data.status!=1){
				layer.msg(data.info);
				return false;
			}else{
				cityData = data.info;
				cityRegNum = eval(cityData.cityRegNum);
				cityLoanMoney = eval(cityData.cityLoanMoney);
				cityRepayMoney = eval(cityData.cityRepayMoney);
			    var s = echarts.init(document.getElementById("echarts-map-chart"))
			      , c = {
			        title: {
			            text: "用户地域分布",
			            x: "center"
			        },
			        tooltip: {
			            trigger: "item"
			        },
			        legend: {
			            orient: "vertical",
			            x: "left",
			            data: ["注册用户", "放款金额", "还款金额"]
			        },
			        series: [{
			            name: "注册用户",
			            type: "map",
			            mapType: "china",
			            roam: !1,
			            itemStyle: {
			                normal: {
			                    label: {
			                        show: !0
			                    }
			                },
			                emphasis: {
			                    label: {
			                        show: !0
			                    }
			                }
			            },
			            data: cityRegNum
			        }, {
			            name: "放款金额",
			            type: "map",
			            mapType: "china",
			            itemStyle: {
			                normal: {
			                    label: {
			                        show: !0
			                    }
			                },
			                emphasis: {
			                    label: {
			                        show: !0
			                    }
			                }
			            },
			            data: cityLoanMoney
			        }, {
			            name: "还款金额",
			            type: "map",
			            mapType: "china",
			            itemStyle: {
			                normal: {
			                    label: {
			                        show: !0
			                    }
			                },
			                emphasis: {
			                    label: {
			                        show: !0
			                    }
			                }
			            },
			            data: cityRepayMoney
			        }]
			    };
			    s.setOption(c),
			    $(window).resize(s.resize);
			}
		}
	);

	
	var w = echarts.init(document.getElementById("echarts-funnel-chart"))
      , b = {
		  title: {
            text: "还款方式",
			x: "right"
        },
		 tooltip: {
        trigger: 'item',
        formatter: "{a} <br/>{b}: {c} ({d}%)"
    },
    legend: {
        orient: 'vertical',
        x: 'right',
		y:50+'px',
        data:['支付宝转账']
    },
    series: [
        {
            name:'支付宝转账',
            type:'pie',
            radius: ['100%'],
            avoidLabelOverlap: true,
            label: {
                normal: {
                    show: false,
                    position: 'left'
                },
                emphasis: {
                    show: true,
                    textStyle: {
                        fontSize: '30',
                        fontWeight: 'bold'
                    }
                }
            },
            labelLine: {
                normal: {
                    show: true
                }
            },
            data:[
                {value:234, name:'支付宝转账'},
            ]
       	 }
    	]
	  }
	  w.setOption(b),
      $(window).resize(w.resize)
});
